import React, { useEffect, useState } from "react";
import moviesApi from "../../utils/MoviesApi.js";

function SearchForm(props) {
    const [value, setValue] = React.useState('');
    const [valueValid, setValueValid] = React.useState(false);



    const [moviesList, setMoviesList] = useState([]);


    // Обработчик изменения инпута обновляет стейт
    function handleChange(e) {
        setValue(e.target.value);
    }

    useEffect(() => {
        moviesApi.getInitialCards()
            .then((cardsInfo) => {
                setMoviesList(cardsInfo);
            })
            .catch((err) => {
                console.log(`Внимание! ${err}`);
            });
    }, []);

    function useValidation(value, validations) {
        const [isEmpty, setIsEmpty] = useState(true)
        useEffect( () => {
            for (const validation in validations) {
                switch (validations) {
                    case 'isEmpty':
                        value ? setIsEmpty(false) : setIsEmpty(true)
                        break
                }
            }
        }, [value])
    }

    function handleSubmit(evt) {
        evt.preventDefault();
        console.log('поиск');
        if (!value) {
            setValueValid(true);
        } else {
            setValueValid(false);
        }
      //  setValueValid(false); // но это должно происходить после проверки валидации, то есть тут должна быть функция проверки валидации
    }


    return (
        <section className="search">
            <form className="search__form" onSubmit={handleSubmit}>
                <div className="search__logo" />
                <div className="search__container">
                    <input className="search__input"
                    placeholder='Фильм'
                    type='search'
                    required
                    name="search"
                    value={value || ""} 
                    onChange={handleChange}
                    />
                    <button className="search__start" type="submit">
                    </button>
                </div>
                <label className="filter">
                    <input className="filter__checkbox" type="checkbox"></input>
                    <span className="filter__checkbox-slider"></span>
                    <span className="filter__text">Короткометражки</span>
                </label>
            </form>
            <span className="error-message">{valueValid? "" : "Нужно ввести ключевое слово"}</span>
        </section>
    );
}

export default SearchForm;
