import React from "react";
import { useFormValidation } from "../../utils/formValidation.js";

function SearchForm(props) {
    const [value, setValue] = React.useState('');
    const { values, handleChange, errors, isValid, resetForm } = useFormValidation();

    // Обработчик изменения инпута обновляет стейт

    function handleSubmit(evt) {
        evt.preventDefault();
        console.log('поиск');
    }


    return (
        <form className="search__form" onSubmit={handleSubmit}>
            <div className="search__logo" />
            <div className="search__container">
                <input className="search__input"
                placeholder='Фильм'
                type='search'
                required
                name="name"
                value={values.name || ""}
                onChange={handleChange}/>
                <button className="search__start" type="submit" disabled={!isValid}>
                </button>
            </div>
            <label className="filter">
                <input className="filter__checkbox" type="checkbox"></input>
                <span className="filter__checkbox-slider"></span>
                <span className="filter__text">Короткометражки</span>
            </label>
        </form>
    );
}

export default SearchForm;
