import React from "react";
import { Link } from "react-router-dom";
import { CurrentUserContext } from "../../contexts/CurrentUserContext";
import { useFormValidation } from "../../utils/formValidation.js";

function Profile(props) {
    const  currentUser = React.useContext(CurrentUserContext);
    const { values, handleChange, errors, isValid, resetForm, setValues } = useFormValidation();

    function hahdleSubmit(evt) {
        evt.preventDefault();
        console.log(1);
        resetForm();
        props.onUpdateUser({
            name: values.name,
            email: values.email,
        })
    }

    function handleInputChange(evt) {
        console.log(1);
        handleChange(evt);
    }

    React.useEffect(() => {
        setValues(currentUser);
      }, [currentUser, setValues]);



    return (
        <section className="profile">
            <h2 className="profile__name">Привет, {currentUser.name}!</h2>
            <form className="profile__form" onSubmit={hahdleSubmit}>
                <div className="profile__item">
                    <label className="profile__label">Имя</label>
                    <input  className="profile__input" 
                            type="name"
                            name="name" required minLength="2" maxLength="30"
                            placeholder={currentUser.name}
                            value={values.name || ""} 
                            onChange={handleInputChange}>
                    </input>
                </div>
                <div className="profile__item">
                    <label className="profile__label">E-mail</label>
                    <input  className="profile__input"
                            type="email"
                            name="email" required minLength="2" maxLength="30" 
                            placeholder={currentUser.email}
                            value={values.email || ""} 
                            onChange={handleInputChange}>
                    </input>
                </div>
                <div className="profile__links">
                    <button className={isValid ? "profile__link" : "profile__link profile__link_invalid"}>Редактировать</button>
                </div>
            </form>
            <button className="profile__button" onClick={props.signOut}>Выйти из аккаунта</button>
        </section>
    );
}

export default Profile;
