import { useLocation } from 'react-router';
import React, { useEffect, useState } from "react";
import moviesApi from "../../utils/MoviesApi.js";
import MoviesCard from "../MoviesCard/MoviesCard.js";
import Preloader from "../Preloader/Preloader.js";


function MoviesCardList(props) {
    const routes  = useLocation();
    const [moviesList, setMoviesList] = useState([]);
    const [moviesCount, setMoviesCount] = useState(12);

    useEffect(() => {
        moviesApi.getInitialCards()
            .then((cardsInfo) => {
                setMoviesList(cardsInfo);
            })
            .catch((err) => {
                console.log(`Внимание! ${err}`);
            });
    }, []);

    function handleClick() {
        setMoviesCount(moviesCount+3);
    }

    return (
        <>
        {
            moviesList.length === 0 ?
            (<Preloader />) :
            (<section className="movies-list">
            {moviesList.map((item, index) => {
              if (index + 1 <= moviesCount) {
                return <MoviesCard film={item} key={index} />;
              } else {
                return '';
              }
            })}
            </section>)
        }
        { moviesCount < 30 && 0 < moviesCount && routes.pathname === "/movies" &&  (
            <section className="another-button__container">
                <button className="another-button" onClick={props.handleClick}>Ещё</button>
            </section>
        )}
        </>
    );
}

export default MoviesCardList;
