import { useLocation } from 'react-router';
import React, { useEffect, useState } from "react";
import moviesApi from "../../utils/MoviesApi.js";
import MoviesCard from "../MoviesCard/MoviesCard.js";
import Preloader from "../Preloader/Preloader.js";


function MoviesCardList({filterMovies, moviesList, setMoviesCount, moviesCount, favoriteList}) {
    const routes  = useLocation();
    const { pathname } = useLocation();


    function handleClick() {
        setMoviesCount(moviesCount+3);
    }

    return (
      <>
        {
          moviesList.length === 0 ? 
          (<Preloader/>) :
          (
            <section className="movies-list">
              {pathname === '/movies' && filterMovies &&
                  filterMovies.map((item, index) => {
                    if (index + 1 <= moviesCount) {
                      return (
                        <MoviesCard
                          movie={item}
                          key={index}
                          handleSaveMovies={handleSaveMovies}
                        />
                      );
                    } else {
                      return '';
                    }
                  })}{' '}
              {pathname === '/saved-movies' && favoriteList &&
                favoriteList.map((item, index) => {
                  if (index + 1 <= moviesCount) {
                    return (
                      <MoviesCard
                        movie={item}
                        key={index}
                      />
                    );
                  } else {
                    return '';
                  }
                })}
            </section>
          )
        }
        { 0 < moviesCount && moviesCount < 30 && moviesCount < moviesList.length  && routes.pathname === "/movies" &&   (
            <section className="another-button__container">
                <button className="another-button" onClick={handleClick}>Ещё</button>
            </section>
        )}

      </>

    );
}

export default MoviesCardList;


