import React, { useEffect, useState } from "react";
import moviesApi from "../../utils/MoviesApi.js";
import MoviesCard from "../MoviesCard/MoviesCard.js";
import MoviesCardList from "../MoviesCardList/MoviesCardList";
import SearchForm from "../SearchForm/SearchForm";
import Footer from "../Footer/Footer";
import Header from "../Header/Header";
import Navigation from "../Navigation/Navigation";

function Movies({onHeaderOpen}) {
    const [moviesCount, setMoviesCount] = useState(0);
    const [moviesList, setMoviesList] = useState([]);
    const [value, setValue] = React.useState('');


    useEffect(() => {
        moviesApi.getInitialCards()
            .then((cardsInfo) => {
                setMoviesList(cardsInfo);
            })
            .catch((err) => {
                console.log(`Внимание! ${err}`);
            });
    }, []);


    const filterMovies = moviesList.filter(movie => {
        return movie.nameRu.toLowerCase().includes(value.toLowerCase())
    })

    return (
        <section className="movies">
            <Header>
                <Navigation onHeaderOpen={onHeaderOpen}></Navigation>
            </Header>
            <SearchForm setMoviesCount={setMoviesCount} value={value}></SearchForm>
            <MoviesCardList  setMoviesCount={setMoviesCount} moviesCount={moviesCount} moviesList={moviesList} />
            <Footer></Footer>
        </section>


    );
}

export default Movies;
