import React, { useState, useEffect } from 'react'
import MoviesCardList from "../MoviesCardList/MoviesCardList";
import SearchForm from "../SearchForm/SearchForm";
import Header from "../Header/Header";
import Navigation from "../Navigation/Navigation";
import { CurrentUserContext } from '../../contexts/CurrentUserContext';
import Footer from '../Footer/Footer';



function SavedMovies({ onHeaderOpen,
    setFavoriteList,
    favoriteList,
    handleDeleteMovies,
    moviesList,
    setMoviesList,
    moviesCount,
    setMoviesCount,
    addMovies,
    setAddMovies }) {

    const [value, setValue] = React.useState('');


    const filterMovies = favoriteList.filter(movie => {
        return movie.nameRU.toLowerCase().includes(value.toLowerCase())
    })

    return (
        <section className="movies movies_saved">
            <SearchForm setMoviesCount={setMoviesCount}
                value={value}
                setValue={setValue}
                setMoviesList={setMoviesList}
                filterMovies={filterMovies}>
            </SearchForm>
            <MoviesCardList
                favoriteList={favoriteList}
                handleDeleteMovies={handleDeleteMovies}
                moviesList={moviesList}
                setMoviesList={setMoviesList}
                setMoviesCount={setMoviesCount}
                moviesCount={moviesCount}
                addMovies={addMovies}
                setAddMovies={setAddMovies}
            />
            <Footer></Footer>
        </section>


    );
}

export default SavedMovies;
