import { useLocation } from 'react-router';
import React, { useEffect, useState } from "react";
import moviesApi from "../../utils/MoviesApi.js";
import MoviesCard from "../MoviesCard/MoviesCard.js";
import Preloader from "../Preloader/Preloader.js";
import ScreenSize from '../../hooks/ScreenSize.js';


function MoviesCardList({
  moviesList,
  setMoviesCount,
  moviesCount,
  favoriteList,
  handleSaveMovies,
  handleDeleteMovies,
  addMovies,
  setAddMovies
}) {

  const width = ScreenSize()
  const { pathname } = useLocation();

/*
  useEffect(() => {
    function getCards() {
      if (width >= 1280) {
        setMoviesCount(12);
        setAddMovies(3);
      } else if (width < 1279 && width >= 767) {
        setMoviesCount(8);
        setAddMovies(2);
      } else if (width <= 766) {
        setMoviesCount(5);
        setAddMovies(1);
      }
    }
    getCards();
  }, [width]);
  */



  function handleClick() {
    setMoviesCount(moviesCount + addMovies);
  }

  return (
    <>
      {
        moviesList.length === 0 ?
          (<Preloader />) :
          (
            <section className="movies-list">
              {pathname === '/movies' && moviesList &&
                moviesList.map((item, index) => {
                  if (index + 1 <= moviesCount) {
                    return (
                      <MoviesCard
                        movie={item}
                        key={index}
                        handleSaveMovies={handleSaveMovies}
                        handleDeleteMovies={handleDeleteMovies}
                        favoriteList={favoriteList}
                      />
                    );
                  } else {
                    return '';
                  }
                })}
              {pathname === '/saved-movies' && favoriteList &&
                favoriteList.map((item, index) => {
                  return (
                    <MoviesCard
                      movie={item}
                      key={index}
                      handleDeleteMovies={handleDeleteMovies}
                      favoriteList={favoriteList}
                    />
                  )
                })}
            </section>
          )
      }
      {pathname === '/movies' && moviesCount < moviesList.length && 0 < moviesCount && (
        <section className="another-button__container">
          <button className="another-button" onClick={handleClick}>Ещё</button>
        </section>
      )}
    </>

  );
}

export default MoviesCardList;

