import { useLocation } from 'react-router';
import React, { useEffect, useState } from "react";
import moviesApi from "../../utils/MoviesApi.js";
import MoviesCard from "../MoviesCard/MoviesCard.js";
import Preloader from "../Preloader/Preloader.js";


function MoviesCardList(props) {
    const routes  = useLocation();

    function handleClick() {
        props.setMoviesCount(props.moviesCount+3);
    }

    return (
        <>
        {
            props.moviesList.length === 0 ?
            (<Preloader/>) :
            (<section className="movies-list">
            {props.filterMovies.map((item, index) => {
              if (index + 1 <= props.moviesCount) {
                return <MoviesCard movie={item} key={index} handleSaveMovies={props.handleSaveMovies} />;
              } else {
                return '';
              }
            })}
            </section>)
        }
        { 0 < props.moviesCount && props.moviesCount < 30 && routes.pathname === "/movies" &&   (
            <section className="another-button__container">
                <button className="another-button" onClick={handleClick}>Ещё</button>
            </section>
        )}
        </>
    );
}

export default MoviesCardList;


// добавить вариант с выводом на экран "ничего не найдено"
// убрать конпку "еще" в случае, если нет поиска
// добавить вариант поиска по английскому наименованию
// сделать так, чтобы результат поиска при последующих поисках не осуществлялся
// рендер карточек фильмов в зависимости от размеров экрана
// при нажатии на карточку происходит переадресация на трейлер фильма
