import { Link } from "react-router-dom";
import { CurrentUserContext } from "../../contexts/CurrentUserContext";

function Profile(props) {
    const  currentUser = React.useContext(CurrentUserContext);

    function hahdleSubmit(evt) {
        evt.preventDefault();
        console.log(1)
    }
    return (
        <>
        <section className="profile">
            <h2 className="profile__name">Привет, {currentUser.name}!</h2>
            <form className="profile__form" onSubmit={hahdleSubmit}>
                <div className="profile__item">
                    <label className="profile__label">Имя</label>
                    <input className="profile__input" type="name" placeholder={currentUser.name}></input>
                </div>
                <div className="profile__item">
                    <label className="profile__label">E-mail</label>
                    <input className="profile__input" type="email" placeholder={currentUser.email}></input>
                </div>
