import React from "react";
import { Navlink, useLocation } from "react-router-dom"

function MoviesCard(props) {
    const routes = useLocation();

    function handleSaveMovies() {
        props.onCardSave(props.movie)
    }

    return (
        <figure className="movies-card">
            <figcaption className="movies-card__caption">
                <div className="movies-card__text">
                    <p className="movies-card__title">33 слова о дизайне</p>
                    <p className="movies-card__length">1ч 47м</p>
                </div>
                    {
                        routes.pathname === '/movies' ? (
                            <button className={cardSaveButtonClassName} type="button" onClick={handleSaveMovies}>
                            </button>
                        ) :
                        (
                            <button className="movies-card__button movies-card__button_delete" type="button">
                            </button>
                        )
                    }
            </figcaption>
            <a className="movies-card__link">
                <img className="movies-card__img" src={props.film} alt="Превью фильма"/>
            </a>
        </figure>
    );
}

export default MoviesCard;
