import React, { useState, useEffect} from 'react'
import MoviesCardList from "../MoviesCardList/MoviesCardList";
import SearchForm from "../SearchForm/SearchForm";
import Header from "../Header/Header";
import Navigation from "../Navigation/Navigation";
import {CurrentUserContext} from '../../contexts/CurrentUserContext';
import Footer from '../Footer/Footer';


function SavedMovies({onHeaderOpen, favoriteList}) {
    // подписка компонента на контекст
    const currentUser = React.useContext(CurrentUserContext);
    const [favoriteMoviesList, setFavoriteMoviesList] = useState([])

    useEffect(() => {
        localStorage.savedMovies &&  setFavoriteMoviesList(JSON.parse(localStorage.getItem('savedMovies')))
    }, []);



    return (
        <section className="movies movies_saved">
            <SearchForm></SearchForm>
            <MoviesCardList favoriteList={favoriteList}/>
            <Footer></Footer>
        </section>


    );
}

export default SavedMovies;
