import React, { useEffect, useState } from "react";
import moviesApi from "../../utils/MoviesApi.js";
import MoviesCard from "../MoviesCard/MoviesCard.js";
import MoviesCardList from "../MoviesCardList/MoviesCardList";
import SearchForm from "../SearchForm/SearchForm";
import Footer from "../Footer/Footer";
import Header from "../Header/Header";
import Navigation from "../Navigation/Navigation";
import Preloader from "../Preloader/Preloader.js";
import { useLocation } from 'react-router';

function Movies({onHeaderOpen, 
    handleSaveMovies, 
    handleDeleteMovies, 
    moviesList, 
    setMoviesList, 
    favoriteList,
    moviesCount, 
    setMoviesCount,
    addMovies, 
    setAddMovies  }) {
        const routes  = useLocation()

    const [value, setValue] = React.useState('');
    const [isLoading, setIsLoading] = React.useState(false)

    const queryData = localStorage.getItem('query');
    const [queryString, setQueryString] = useState(queryData && routes.pathname === '/movies' ? queryData : '');


    // Эффект обработки запроса от формы поиска
    useEffect(() => {
        if (queryString) {
            const filterMovies = moviesList.filter((movie) =>
                movie.nameRU.toLowerCase().indexOf(queryString.toLowerCase()) > -1)
                if (filterMovies.length) {
                    setMoviesList(filterMovies)
                    localStorage.setItem('listOfFound', JSON.stringify(filterMovies))
                } else {
                  //  setMessage('Ничего не найдено')
                    setMoviesList([])
                } 
            }
        }, [queryString])



    useEffect(() => {
        moviesApi.getInitialCards()
            .then((movie) => {
              //  console.log(movie)
                setMoviesList(movie);
            })
            .catch((err) => {
                console.log(`Внимание! ${err}`);
            })
    }, []);

    function handleSubmitSearchForm (query) {
        setQueryString(query)
    }


    return (
        <section className="movies">
            <SearchForm setMoviesCount={setMoviesCount} 
                        value={value} 
                        setValue={setValue} 
                        setMoviesList={setMoviesList}
                        handleSubmitSearchForm={handleSubmitSearchForm}
                        >
            </SearchForm>
            <MoviesCardList  
            setMoviesCount={setMoviesCount} 
            moviesCount={moviesCount} 
            moviesList={moviesList} 
           // filterMovies={filterMovies} 
            handleSaveMovies={handleSaveMovies} 
            handleDeleteMovies={handleDeleteMovies} 
            favoriteList={favoriteList}
            addMovies={addMovies}
            setAddMovies={setAddMovies}
            />
            <Footer></Footer>
        </section>


    );
}

export default Movies;

