import React, { useState } from "react";
import { Navlink, useLocation } from "react-router-dom";
import { mainApi } from '../../utils/MainApi.js';
import { CurrentUserContext } from "../../contexts/CurrentUserContext";

function MoviesCard({film}) {
    const currentUser = React.useContext(CurrentUserContext);
    const [isSavedMovies, setIsSavedMovies] = useState(false);
    const routes = useLocation();

    const cardSaveButtonClassName = `movies-card__button movies-card__button_save ${
        isSavedMovies ? "movies-card__button_save_active" : " "
      }`;

    function handleSaveMovies() {
        setIsSavedMovies(isSavedMovies === false ? true : false);
        mainApi.savedMovies(film).
            then((res) => {
            console.log(1);
          })
          .catch((err) => {
            console.log(`Внимание! ${err}`);
          })
    }

    function handleDeleteMovies() {
        mainApi.deleteMovies(currentUser.id).
        then((res) => {
            console.log(1);
          })
          .catch((err) => {
            console.log(`Внимание! ${err}`);
          })
    }


    function calculateTime () {
        return `${Math.floor(film.duration / 60)}ч ${film.duration % 60}м`;
    };


    return (
        <figure className="movies-card">
            <figcaption className="movies-card__caption">
                <div className="movies-card__text">
                    <p className="movies-card__title">{film.nameRU}</p>
                    <p className="movies-card__length">{calculateTime()}</p>
                </div>
                    {
                        routes.pathname === '/movies' ? (
                            <button className={cardSaveButtonClassName} type="button" onClick={handleSaveMovies}>
                            </button>
                        ) :
                        (
                            <button className="movies-card__button movies-card__button_delete" type="button" onClick={handleDeleteMovies}>
                            </button>
                        )
                    }
            </figcaption>
            <a className="movies-card__link" href={film.trailerLink} target="_blank">
                <img className="movies-card__img" src={`https://api.nomoreparties.co/${film.image.formats.thumbnail.url}`} alt={film.nameRu}/>
            </a>
        </figure>
    );
}

export default MoviesCard;
